mapper.select:
SELECT instruction_id, payer_account, payee_account, currency, amount, purpose, channel, batch_id, priority, risk_score, status, created_at
FROM payments
WHERE instruction_id = #{instructionId}

mapper.update:
UPDATE payments
SET status = #{status}
WHERE instruction_id = #{instructionId}

UPDATE payments
SET status = #{status}, risk_score = #{riskScore}
WHERE instruction_id = #{instructionId}
